import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
const API = `https://jsonplaceholder.typicode.com/comments?_limit=10 `;

const Comments = () => {
  const [comments, setComments] = useState([]);
  const [error, setError] = useState();
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    try {
      const getComments = async () => {
        const response = await fetch(API);
        const data = await response.json();
        setComments(data);
      };
      getComments();
    } catch (error) {
      setError(error.massage);
    } finally {
      setIsLoading(false);
    }
  }, []);

  if (error) {
    return <div>Error:{error}</div>;
  }

  return (
    <Conteiner>
      {isLoading ? (
        <IsLoading>loading</IsLoading>
      ) : (
        <>
          <Heading>Comments</Heading>
          <Table>
            <tbody>
              <TrHeading>
                <TdHeading>name</TdHeading>
                <TdHeading>email</TdHeading>
                <TdHeading>body</TdHeading>
                <TdHeading>get more</TdHeading>
              </TrHeading>
              {comments.map(({ name, email, id }) => (
                <Tr key={id}>
                  <Td>{name}</Td>
                  <Td>{email}</Td>
                  <Td>{email}</Td>
                  <Td>
                    <Link className="data" to={id.toString()}>
                      Full Information
                    </Link>
                  </Td>
                </Tr>
              ))}
            </tbody>
          </Table>
        </>
      )}
    </Conteiner>
  );
};

export default Comments;
const Table = styled.table`
  border: 1px solid black;
  margin-left: 4rem;
  width: 60rem;
  border-collapse: collapse;
  box-shadow: 0 0 15px 2px black;
  bor
`;
const TrHeading = styled.tr`
  background-color: green;
`;
const Tr = styled.tr`
  cursor: pointer;
`;
const Conteiner = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const Heading = styled.h1`
  font-size: 5rem;
`;
const Td = styled.td`
  border: 1px solid black;
  border-top: none;
  border-left: none;
  padding: 10px;
  width: 100%;
`;
const TdHeading = styled.td`
  padding: 10px;
`;
const IsLoading = styled.div`
  font-size: 6rem;
  background-color: #fcba04;
  padding: 1rem;
  border-radius: 1rem;
  width: 80%;
`;
